package dev.tsnanh.vku.view.optionsimport android.os.Bundleimport android.view.LayoutInflaterimport android.view.Viewimport android.view.ViewGroupimport androidx.databinding.DataBindingUtilimport androidx.fragment.app.Fragmentimport androidx.fragment.app.viewModelsimport androidx.lifecycle.Observerimport dev.tsnanh.vku.Rimport dev.tsnanh.vku.databinding.FragmentOptionsBindingclass OptionsFragment : Fragment() {    private lateinit var binding: FragmentOptionsBinding    private val viewModel: OptionsViewModel by viewModels()    override fun onCreateView(        inflater: LayoutInflater,        container: ViewGroup?,        savedInstanceState: Bundle?    ): View? {        binding = DataBindingUtil            .inflate(inflater, R.layout.fragment_options, container, false)        return binding.root    }    override fun onViewCreated(view: View, savedInstanceState: Bundle?) {        super.onViewCreated(view, savedInstanceState)        binding.viewModel = viewModel        viewModel.user.observe(viewLifecycleOwner, Observer {            it?.let {                binding.user = it            }        })    }}